package org.monke.connector;

import com.fasterxml.jackson.core.JsonProcessingException;
import com.fasterxml.jackson.databind.ObjectMapper;
import org.junit.jupiter.api.Test;
import org.monke.connector.model.Issue;

import java.io.IOException;
import java.io.InputStream;
import java.time.Instant;

import static org.junit.jupiter.api.Assertions.*;

public class SerializationTest {

    private final ObjectMapper objectMapper = new ObjectMapper();

    @Test
    public void issueSerializationTest() throws IOException {
        // Charger le fichier JSON
        InputStream inputStream = getClass().getResourceAsStream("/issue.json");
        assertNotNull(inputStream, "Le fichier issue.json devrait être trouvé dans les ressources");

        // Désérialiser le JSON en objet Issue
        Issue issue = objectMapper.readValue(inputStream, Issue.class);

        // Vérifier quelques champs pour s'assurer que la désérialisation est correcte
        assertEquals(1, issue.getId());
        assertEquals(1347, issue.getNumber());
        assertEquals("Found a bug", issue.getTitle());
        assertEquals("open", issue.getState());
        assertEquals("I'm having a problem with this.", issue.getBody());
        assertFalse(issue.getLocked());
        assertEquals(0, issue.getComments());
        assertEquals(Instant.parse("2011-04-22T13:33:48Z"), issue.getCreatedAt());

        // Vérifier l'assigné
        assertNotNull(issue.getAssignee());
        assertEquals("octocat", issue.getAssignee().getLogin());
        assertEquals(1, issue.getAssignee().getId());

        // Vérifier le milestone
        assertNotNull(issue.getMilestone());
        assertEquals(1002604, issue.getMilestone().getId());
        assertEquals("v1.0", issue.getMilestone().getTitle());

        // Vérifier les labels
        assertNotNull(issue.getLabels());
        assertEquals(1, issue.getLabels().size());
        assertEquals("bug", issue.getLabels().get(0).getName());
        assertEquals("f29513", issue.getLabels().get(0).getColor());
        assertTrue(issue.getLabels().get(0).get_default());
    }
}

